import React, { useState, useEffect } from 'react';
import './App.css';
import CategoryManager from './components/CategoryManager';
import TaskManager from './components/TaskManager';
import Login from './components/Login';
import axios from 'axios';

const API_BASE_URL = 'http://localhost:8000/api';

function App() {
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [checkingAuth, setCheckingAuth] = useState(true);
  const [categories, setCategories] = useState([]);
  const [tasks, setTasks] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [loading, setLoading] = useState(false);

  // Check authentication status on mount
  useEffect(() => {
    checkAuth();
  }, []);

  const checkAuth = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/auth.php`, {
        withCredentials: true
      });

      if (response.data.authenticated && response.data.user) {
        setUser(response.data.user);
        setIsAuthenticated(true);
      } else {
        setIsAuthenticated(false);
      }
    } catch (error) {
      console.error('Auth check failed:', error);
      setIsAuthenticated(false);
    } finally {
      setCheckingAuth(false);
    }
  };

  const handleLogin = (userData) => {
    setUser(userData);
    setIsAuthenticated(true);
  };

  const handleLogout = async () => {
    try {
      await axios.delete(`${API_BASE_URL}/auth.php`, {
        withCredentials: true
      });
      setUser(null);
      setIsAuthenticated(false);
      setCategories([]);
      setTasks([]);
      setSelectedCategory(null);
    } catch (error) {
      console.error('Logout failed:', error);
      // Still clear local state even if API call fails
      setUser(null);
      setIsAuthenticated(false);
      setCategories([]);
      setTasks([]);
      setSelectedCategory(null);
    }
  };

  // Fetch categories
  const fetchCategories = async () => {
    try {
      const response = await axios.get(`${API_BASE_URL}/categories.php`, {
        withCredentials: true
      });
      setCategories(response.data);
    } catch (error) {
      console.error('Error fetching categories:', error);
      if (error.response?.status === 401) {
        // Session expired, logout
        handleLogout();
      }
    }
  };

  // Fetch tasks
  const fetchTasks = async (categoryId = null) => {
    try {
      setLoading(true);
      const url = categoryId 
        ? `${API_BASE_URL}/tasks.php?category_id=${categoryId}`
        : `${API_BASE_URL}/tasks.php`;
      const response = await axios.get(url, {
        withCredentials: true
      });
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
      if (error.response?.status === 401) {
        // Session expired, logout
        handleLogout();
      }
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (isAuthenticated) {
      fetchCategories();
      fetchTasks();
    }
  }, [isAuthenticated]);

  const handleCategorySelect = (categoryId) => {
    setSelectedCategory(categoryId);
    fetchTasks(categoryId);
  };

  const handleCategoryChange = () => {
    fetchCategories();
    if (selectedCategory) {
      fetchTasks(selectedCategory);
    } else {
      fetchTasks();
    }
  };

  const handleTaskChange = () => {
    if (selectedCategory) {
      fetchTasks(selectedCategory);
    } else {
      fetchTasks();
    }
  };

  // Show loading state while checking authentication
  if (checkingAuth) {
    return (
      <div className="App">
        <div style={{ 
          display: 'flex', 
          justifyContent: 'center', 
          alignItems: 'center', 
          minHeight: '100vh',
          color: 'white',
          fontSize: '18px'
        }}>
          Loading...
        </div>
      </div>
    );
  }

  // Show login page if not authenticated
  if (!isAuthenticated) {
    return <Login onLogin={handleLogin} />;
  }

  return (
    <div className="App">
      <header className="App-header">
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', maxWidth: '1400px', margin: '0 auto', width: '100%' }}>
          <div>
            <h1>Task Management System</h1>
            <p>Organize your tasks efficiently</p>
          </div>
          <div style={{ display: 'flex', alignItems: 'center', gap: '15px' }}>
            <span style={{ fontSize: '16px', opacity: 0.9 }}>
              Welcome, <strong>{user?.username}</strong>
            </span>
            <button 
              onClick={handleLogout}
              style={{
                padding: '8px 16px',
                background: 'rgba(255, 255, 255, 0.2)',
                border: '2px solid white',
                borderRadius: '6px',
                color: 'white',
                cursor: 'pointer',
                fontSize: '14px',
                fontWeight: '600',
                transition: 'all 0.3s'
              }}
              onMouseOver={(e) => {
                e.target.style.background = 'rgba(255, 255, 255, 0.3)';
              }}
              onMouseOut={(e) => {
                e.target.style.background = 'rgba(255, 255, 255, 0.2)';
              }}
            >
              Logout
            </button>
          </div>
        </div>
      </header>

      <div className="App-container">
        <div className="sidebar">
          <CategoryManager
            categories={categories}
            onCategoryChange={handleCategoryChange}
            selectedCategory={selectedCategory}
            onCategorySelect={handleCategorySelect}
          />
        </div>

        <div className="main-content">
          <TaskManager
            tasks={tasks}
            categories={categories}
            selectedCategory={selectedCategory}
            onTaskChange={handleTaskChange}
            loading={loading}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
